import React from "react";
import { StyleSheet, View, ImageBackground, Text, Image } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import PageSubitle from "./PageSubtitle";
import { useFonts } from "expo-font";
import { FontAwesomeIcon } from "@fortawesome/react-native-fontawesome";
import {
  faChevronRight,
  faCaretLeft,
  faCheck,
} from "@fortawesome/free-solid-svg-icons";

export default function PodcastsPage({ navigation }) {
  const titre = "Affronte ta peur";
  const fait = 0;
  const [loaded] = useFonts({
    CormorantGaramond: require("../assets/font/CormorantGaramond-Bold.ttf"),
  });

  if (!loaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require("../assets/images/fond_noir.png")}
        style={styles.fondNoir}
      >
        <FontAwesomeIcon
          icon={faCaretLeft}
          color={"#D3FCF7"}
          size={55}
          style={styles.btn}
          onPress={() => navigation.goBack()}
        />
        <Text
          style={{
            marginTop: "17%",
            textAlign: "center",
            marginBottom: 25,
            fontSize: 17,
            fontFamily: "CormorantGaramond",
          }}
        >
          Challenge de la semaine : {"\n"} {titre}
        </Text>
        <PageSubitle content="Challenge de la semaine" />
        <View style={styles.challenge}>
          <Text style={{ color: "white", alignSelf: "center" }}>{titre}</Text>
        </View>
        <Image
          source={require("../assets/images/artiste.jpg")}
          style={{ width: "80%", height: "15%", marginBottom: 10 }}
        />
        <PageSubitle content="Détail du challenge de la semaine" />
        <Text style={styles.description}>
          Cette semaine tu vas devoir affronter tes peurs.
        </Text>
        <Text style={styles.description}>
          Choisis la peur que tu te sens d'affronter. Prends un papier et un
          crayon et matérialise par des mots cette peur. Écris ensuite tout
          autour de cette peur toutes les causes qui lui sont liées et essaye de
          trouver une solution à chacune d'elles.
        </Text>
        <Text style={styles.description}>
          Parmis toutes les solutions que tu as trouvé, si tu en as envie, tu
          peux essayer d'en mettre une en pratique.
        </Text>
        <Text style={styles.description}>
          Tu peux montrer la réussite où l'avancée de ton challenge sur les
          réseaux et dans la section "La peur" du forum à travers les hashtags
          #ChallengePeurRU #ChallengeAcceptedRU #ChallengeReussiRU
        </Text>
        {fait === 0 ? (
          <TouchableOpacity
            style={styles.viewBtnNav}
            onPress={() => alert("ok")}
          >
            <Text style={styles.btnNav}>Terminer mon challenge</Text>
          </TouchableOpacity>
        ) : (
          <View style={styles.viewBtnNav}>
            <View style={styles.btnNav}>
              <FontAwesomeIcon icon={faCheck} color={"white"} />
              <Text style={{ color: "white", marginLeft: 10 }}>
                Challenge terminé
              </Text>
            </View>
          </View>
        )}
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    width: "100%",
    height: "100%",
  },
  item: {
    textAlign: "center",
    padding: 10,
    fontSize: 78,
  },
  fondNoir: {
    resizeMode: "cover",
    height: "100%",
    alignItems: "center",
  },
  challenge: {
    backgroundColor: "#2c4645",
    width: "80%",
    flexDirection: "row",
    alignSelf: "center",
    borderRadius: 5,
    minHeight: 50,
    justifyContent: "center",
    marginBottom: 20,
  },
  btn: {
    position: "absolute",
    left: "5%",
    top: "7%",
    shadowColor: "black",
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
  },
  viewBtnNav: {
    shadowColor: "black",
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
    marginTop: 10,
  },
  btnNav: {
    overflow: "hidden",
    borderRadius: 25,
    backgroundColor: "#BD7B49",
    textAlign: "center",
    padding: 15,
    margin: 5,
    color: "white",
    flexDirection: "row",
  },
  arrow: {
    alignSelf: "center",
    position: "absolute",
    right: "5%",
  },
  description: {
    width: "80%",
    textAlign: "justify",
    marginBottom: 10,
  },
});
