import React from "react";
import { StyleSheet, View, ImageBackground, Text, Image } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import PageSubitle from "./PageSubtitle";
import { faCaretLeft } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-native-fontawesome";
import { useFonts } from "expo-font";

export default function PodcastsPage({ navigation }) {
  const [loaded] = useFonts({
    CormorantGaramond: require("../assets/font/CormorantGaramond-Bold.ttf"),
  });

  if (!loaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require("../assets/images/fond_noir.png")}
        style={styles.fondNoir}
      >
        <FontAwesomeIcon
          icon={faCaretLeft}
          color={"#D3FCF7"}
          size={55}
          style={styles.btn}
          onPress={() => navigation.goBack()}
        />
        <Text
          style={{
            marginTop: "15%",
            textAlign: "center",
            marginBottom: 25,
            fontSize: 17,
            fontFamily: "CormorantGaramond",
          }}
        >
          Description de l'offre d'abonnement
        </Text>
        <Image
          source={require("../assets/images/ponton.jpg")}
          style={{ width: "100%", height: "15%", marginBottom: 10 }}
        />
        <PageSubitle content="S'élever avec RaiseUp !" />
        <Text style={{ marginBottom: 10 }}>
          S'élever avec RaiseUp c'est : mieux se comprendre, découvrir ses
          valeurs, atteindre ses objectifs, gérer ses émotions et bien plus
          encore. Tout cela est possible avec l'accompagnement RaiseUp.
        </Text>
        <Image
          source={require("../assets/images/artiste.jpg")}
          style={{ width: "85%", height: "17%", marginBottom: 10 }}
        />
        <PageSubitle content="L'accompagnement RaiseUp contient :" />
        <View
          style={{ alignItems: "flex-start", width: "85%", marginBottom: 10 }}
        >
          <Text>{"\u2022"} 1 questionnaire rapide</Text>
          <Text>{"\u2022"} 1 séance de coaching personnel par mois</Text>
          <Text>{"\u2022"} 2 séances en groupe par mois (live)</Text>
          <Text>{"\u2022"} Des exercices proposés par un coach personnel</Text>
          <Text>{"\u2022"} La disparition des publicités</Text>
          <Text>
            {"\u2022"} La section Forum pour échanger librement et sans crainte
            sur les thématiques abordées
          </Text>
        </View>
        <PageSubitle content="Le prix" />
        <Text style={styles.prix}>25 € /mois</Text>
        <TouchableOpacity style={styles.viewBtnNav} onPress={() => alert("ok")}>
          <Text style={styles.btnNav}>Je veux m'élever avec un pro</Text>
        </TouchableOpacity>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    width: "100%",
    height: "100%",
  },
  item: {
    textAlign: "center",
    padding: 10,
    fontSize: 78,
  },
  fondNoir: {
    resizeMode: "cover",
    height: "100%",
    justifyContent: "center",
    alignItems: "center",
  },
  ensemble: {
    width: "90%",
    height: "65%",
    backgroundColor: "green",
    alignItems: "center",
  },
  viewBtnNav: {
    shadowColor: "black",
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
  },
  btnNav: {
    overflow: "hidden",
    borderRadius: 25,
    backgroundColor: "#D3FCF7",
    textAlign: "center",
    padding: 15,
    margin: 5,
    color: "black",
  },
  prix: {
    color: "#BD7B49",
    marginBottom: 10,
    fontWeight: "bold",
    fontSize: 25,
  },
  btn: {
    position: "absolute",
    left: "5%",
    top: "7%",
    shadowColor: "black",
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
  },
});
