import React from "react";
import { StyleSheet, View, ImageBackground, Text, Image } from "react-native";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import PageSubitle from "./PageSubtitle";
import BtnRetour from "../component/BtnRetour";
import { useFonts } from "expo-font";
import IconProfil from "../component/IconProfil";
import PageTitle from "../component/PageTitle";
import { FontAwesomeIcon } from "@fortawesome/react-native-fontawesome";
import { faChevronRight, faCheck } from "@fortawesome/free-solid-svg-icons";

export default function PodcastsPage({ navigation }) {
  const titre = "Affronte ta peur";
  const challenges = ["hier", "avanthier"];
  const [loaded] = useFonts({
    CormorantGaramond: require("../assets/font/CormorantGaramond-Bold.ttf"),
  });

  if (!loaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require("../assets/images/fond_noir.png")}
        style={styles.fondNoir}
      >
        <IconProfil navigation={navigation} />
        <PageTitle content={"Mes challenges"} />
        <BtnRetour navigation={navigation} />
        <View
          style={{
            width: "100%",
            height: "75%",
            marginTop: "50%",
          }}
        >
          <PageSubitle content="Challenge de la semaine" />
          <TouchableOpacity
            style={styles.challenge}
            onPress={() => navigation.navigate("DetailChallenge")}
          >
            <Text style={{ color: "white", alignSelf: "center" }}>{titre}</Text>
            <FontAwesomeIcon
              icon={faChevronRight}
              color={"white"}
              size={15}
              style={styles.arrow}
            />
          </TouchableOpacity>
          <PageSubitle content="Challenges passÃ©s" />
          <ScrollView contentContainerStyle={{ height: "100%" }}>
            {challenges.map((challenge, index) => (
              <View style={styles.challenge} key={index}>
                <Text style={{ color: "white", alignSelf: "center" }}>
                  {challenge}
                </Text>
                <FontAwesomeIcon
                  icon={faCheck}
                  color={"white"}
                  size={15}
                  style={styles.arrow}
                />
              </View>
            ))}
          </ScrollView>
        </View>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    width: "100%",
    height: "100%",
  },
  item: {
    textAlign: "center",
    padding: 10,
    fontSize: 78,
  },
  fondNoir: {
    resizeMode: "cover",
    height: "100%",
    justifyContent: "center",
    alignItems: "center",
  },
  challenge: {
    backgroundColor: "#2c4645",
    width: "80%",
    flexDirection: "row",
    alignSelf: "center",
    borderRadius: 5,
    minHeight: 50,
    justifyContent: "center",
    marginBottom: 20,
  },
  arrow: {
    alignSelf: "center",
    position: "absolute",
    right: "5%",
  },
});
