import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Image,
  TextInput,
} from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import axios from "axios";

export default function ConnexionPage({ navigation }) {
  const [lastname, setLastname] = useState(null);
  const [firstname, setFirstname] = useState(null);
  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [confirm_pwd, setConfirmPwd] = useState(null);

  const inscription = {
    lastname: lastname,
    firstname: firstname,
    email: email,
    password: password,
    confirm_password: confirm_pwd,
  };

  const register = () => {
    axios
      .post("http://192.168.1.103:3000/register", inscription)
      .then((res) => {
        if (res.ok === 1) {
          navigation.navigate("Menu");
        } else {
          alert(res.message);
        }
      })
      .catch((e) => console.log(e));
  };

  return (
    <View>
      <ImageBackground
        source={require("../assets/images/palme_NB.jpg")}
        style={styles.fondPalme}
      >
        <ImageBackground
          source={require("../assets/images/fond_noir.png")}
          style={styles.fondNoir}
        >
          <Image
            source={require("../assets/images/raise_up_logo_FB.png")}
            style={styles.logo}
          />
          <View style={styles.content}>
            <Text style={styles.titre}>Inscription</Text>
            <Text style={styles.item}>Nom</Text>
            <TextInput
              placeholder="Doe"
              style={styles.input}
              onChange={(e) => setLastname(e.target.value)}
            />
            <Text style={styles.item}>Prénom</Text>
            <TextInput
              placeholder="John"
              style={styles.input}
              onChange={(e) => setFirstname(e.target.value)}
            />
            <Text style={styles.item}>Email</Text>
            <TextInput
              placeholder="email@address.com"
              style={styles.input}
              onChange={(e) => setEmail(e.target.value)}
            />
            <Text style={styles.item}>Mot de passe</Text>
            <TextInput
              placeholder="**********"
              style={styles.input}
              onChange={(e) => setPassword(e.target.value)}
              secureTextEntry={true}
            />
            <Text style={styles.item}>Confirmation de mot de passe</Text>
            <TextInput
              placeholder="**********"
              style={styles.input}
              onChange={(e) => setConfirmPwd(e.target.value)}
              secureTextEntry={true}
            />
            <TouchableOpacity
              style={styles.viewBtnNavContent}
              onPress={() => register()}
            >
              <Text style={styles.btnNav}>Connexion</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.viewBtnNav}>
            <Text
              onPress={() => navigation.navigate("Menu")}
              style={styles.btnNav}
            >
              Connexion avec Facebook
            </Text>
            <Text
              onPress={() => navigation.navigate("Menu")}
              style={styles.btnNav}
            >
              Connexion avec Gmail
            </Text>
          </View>
        </ImageBackground>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    width: "100%",
    height: "100%",
  },
  fondPalme: {
    resizeMode: "contain",
    height: "100%",
  },
  fondNoir: {
    resizeMode: "cover",
    height: "100%",
    justifyContent: "center",
    alignItems: "center",
  },
  logo: {
    resizeMode: "contain",
    height: "19%",
    position: "absolute",
    top: "5%",
    width: "100%",
  },
  content: {
    position: "absolute",
    top: "20%",
    width: "70%",
    height: "60%",
    borderBottomWidth: 2,
    borderBottomColor: "grey",
  },
  item: {
    margin: 5,
  },
  titre: {
    textAlign: "center",
    fontSize: 20,
  },
  input: {
    borderColor: "#000",
    borderWidth: 1,
    borderRadius: 20,
    backgroundColor: "#f0efec",
    width: "100%",
    height: "10%",
    padding: "5%",
  },
  viewBtnNavContent: {
    position: "relative",
    shadowColor: "black",
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
    width: "80%",
    margin: 10,
    left: 15,
  },
  viewBtnNav: {
    position: "absolute",
    bottom: "5%",
    shadowColor: "black",
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
  },
  btnNav: {
    overflow: "hidden",
    borderRadius: 25,
    backgroundColor: "#D3FCF7",
    textAlign: "center",
    padding: 15,
    margin: 5,
  },
});
