import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  TextInput,
} from "react-native";
import IconProfil from "../component/IconProfil";
import PageTitle from "../component/PageTitle";
import { ScrollView, TouchableOpacity } from "react-native-gesture-handler";
import { Avatar } from "react-native-elements/dist/avatar/Avatar";
import { KeyboardAwareScrollView } from "react-native-keyboard-aware-scroll-view";
import { FontAwesomeIcon } from "@fortawesome/react-native-fontawesome";
import { faAngleDoubleRight } from "@fortawesome/free-solid-svg-icons";

export default function ForumPage({ navigation }) {
  const theme = "La peur";
  const [questions, setQuestions] = useState(["Bonjour", "Michel"]);
  const [answers, setAnswers] = useState([
    "reponse1",
    "reponse2",
    "reponse3",
    "reponse4",
  ]);

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require("../assets/images/fond_noir.png")}
        style={styles.fondNoir}
      >
        <IconProfil navigation={navigation} />
        <PageTitle content={"Forum"} />
        <View style={styles.contour}>
          <View style={styles.interieur}>
            <Text
              style={{
                color: "white",
                fontWeight: "bold",
                marginBottom: 10,
              }}
            >
              Le thème de la semaine
            </Text>
            <Text style={{ color: "white" }}>{theme}</Text>
          </View>
        </View>
        <TouchableOpacity
          style={styles.viewBtnNav}
          onPress={() => navigation.navigate("CreateSubject")}
        >
          <Text style={styles.btnNav}>Créer un sujet</Text>
        </TouchableOpacity>
        <View style={{ width: "100%", height: "60%" }}>
          <KeyboardAwareScrollView
            contentContainerStyle={{ alignItems: "center" }}
          >
            {questions.map((question, index) => (
              <View style={styles.ensemble} key={index}>
                <View style={styles.question}>
                  <View style={{ alignItems: "center" }}>
                    <Avatar
                      rounded
                      source={require("../assets/images/accroupie.jpg")}
                      size="medium"
                      containerStyle={styles.photo}
                    />
                    <Text>nom</Text>
                  </View>
                  <Text style={styles.question_content}>{question}</Text>
                </View>
                <View
                  style={{
                    width: "100%",
                    height: "75%",
                  }}
                >
                  <KeyboardAwareScrollView
                    contentContainerStyle={{ alignItems: "center" }}
                  >
                    {answers.map((answer, index) => (
                      <View style={styles.answer} key={index}>
                        <View style={{ alignItems: "center" }}>
                          <Avatar
                            rounded
                            source={require("../assets/images/accroupie.jpg")}
                            size="medium"
                            containerStyle={styles.photo}
                          />
                          <Text>nom</Text>
                        </View>
                        <Text style={styles.answer_content}>{answer}</Text>
                      </View>
                    ))}
                    <View style={styles.answer} key={index}>
                      <View style={{ alignItems: "center" }}>
                        <Avatar
                          rounded
                          source={require("../assets/images/accroupie.jpg")}
                          size="medium"
                          containerStyle={styles.photo}
                        />
                        <Text>nom</Text>
                      </View>
                      <TextInput
                        style={styles.answer_content}
                        multiline={true}
                      />
                      <FontAwesomeIcon
                        icon={faAngleDoubleRight}
                        style={{ alignSelf: "center", marginLeft: 5 }}
                        onPress={() => alert("ok")}
                        size={20}
                      />
                    </View>
                  </KeyboardAwareScrollView>
                </View>
              </View>
            ))}
          </KeyboardAwareScrollView>
        </View>
      </ImageBackground>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    width: "100%",
    height: "100%",
  },
  input: {
    backgroundColor: "white",
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 25,
    width: "80%",
    height: "30%",
    padding: 15,
    marginBottom: 20,
  },
  fondNoir: {
    resizeMode: "cover",
    height: "100%",
    alignItems: "center",
  },
  contour: {
    borderTopColor: "black",
    borderBottomColor: "black",
    borderTopWidth: 1,
    borderBottomWidth: 1,
    width: "80%",
    height: "11%",
    justifyContent: "center",
    alignItems: "center",
    marginTop: "40%",
  },
  interieur: {
    width: "90%",
    height: "90%",
    backgroundColor: "#2c4645",
    borderRadius: 15,
    alignItems: "center",
    justifyContent: "center",
  },
  viewBtnNav: {
    shadowColor: "black",
    shadowOffset: { width: 0, height: 3 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
  },
  btnNav: {
    overflow: "hidden",
    borderRadius: 25,
    backgroundColor: "#D3FCF7",
    textAlign: "center",
    padding: 15,
    margin: 10,
  },
  ensemble: {
    marginTop: 10,
    marginBottom: 20,
    width: "80%",
    height: 300,
  },
  question: {
    flexDirection: "row",
    backgroundColor: "#d1bdb1",
    height: "25%",
    borderRadius: 5,
    padding: 5,
    width: "100%",
  },
  question_content: {
    width: "75%",
    marginLeft: 10,
    textAlign: "center",
    alignSelf: "center",
  },
  answer: {
    flexDirection: "row",
    backgroundColor: "#f0efec",
    width: "97%",
    padding: 10,
  },
  answer_content: {
    backgroundColor: "white",
    overflow: "hidden",
    marginLeft: 15,
    padding: 5,
    borderRadius: 10,
    width: "70%",
  },
  photo: {
    borderColor: "#BD7B49",
    borderWidth: 4,
  },
});
